mapper.insert:

insert into MatchBoard (
	  boardNo
	, userNo
    , title
    , matchGameType
    , matchRegion
    , membernum
    , matchDate
    , content
    , boardDate)
values (
	  seq_matchBoard_no.nextval
	, #{userNo}
	, #{title}
	, #{matchGameType}
	, #{matchRegion}
	, #{membernum}
	, TO_DATE(#{matchDate}, 'YYYY-MM-DD HH24:MI:SS')
	, #{content}
	, sysdate
)
