mapper.insert:

insert into MatchBoard (
	  boardNo
	, userNo
    , title
    , matchGameType
    , matchRegion
    , membernum
    , matchDate
    , content
    , boardDate
    , count)
values (
	  seq_matchBoard_no.nextval
	, #{userNo}
	, #{title}
	, #{matchGameType}
	, #{matchRegion}
	, #{membernum}
	, TO_DATE(#{matchDate}, 'YYYY-MM-DD HH24:MI:SS')
	, #{content}
	, sysdate
	, 0
)

mapper.update:

UPDATE matchboard
SET count = count + 1
WHERE boardNo = #{boardNo}


UPDATE matchboard
SET status = 1
WHERE boardNo = #{boardNo}

mapper.select:

select status
from matchboard 
where boardNo = #{boardNo}
